mapper.select:
SELECT * FROM `cqj_user` AS u JOIN `gk_questionnaire` AS wj ON (wj.`wj_publisher` = u.userid) AND wj.`status`=0 JOIN `gk_questionnaire_type` AS wjtype ON (wjtype.`wj_type_id` = wj.`wj_type_id`) JOIN `gk_questionnairexx_type` AS wjxxtype ON (wjxxtype.`wjxx_type_id` = wj.`wjxx_type_id`) JOIN `cqj_staff` AS yg ON (yg.staffid = u.usertypeid) AND u.`usertypenub` = 0
AND wj.`wj_object` LIKE concat('%',#{wjObject},'%')
AND yg.`staffname` LIKE concat('%',#{staffname},'%')
AND wj.`wj_fb_time` BETWEEN #{startStr} AND #{endStr}
AND wj.`wj_fb_time` BETWEEN #{startStr} AND NOW()
AND wj.`wj_fb_time` <= #{endStr}
LIMIT #{currentPage},#{pageSize}

SELECT COUNT(*) FROM `cqj_user` AS u JOIN `gk_questionnaire` AS wj ON (wj.`wj_publisher` = u.userid) JOIN `cqj_staff` AS yg ON (yg.staffid = u.usertypeid) AND u.`usertypenub` = 0
AND wj.`wj_object` LIKE concat('%',#{wjObject},'%')
AND yg.`staffname` LIKE concat('%',#{staffname},'%')
AND wj.`wj_fb_time` BETWEEN #{startStr} AND #{endStr}
AND wj.`wj_fb_time` BETWEEN #{startStr} AND NOW()
AND wj.`wj_fb_time` <= #{endStr}
