mapper.insert:
insert into workGrade (id, wId, uId, 
  score, rank, time, 
  status, user1, user2, 
  user3, user4, user5
  )
values (#{id,jdbcType=INTEGER}, #{wid,jdbcType=INTEGER}, #{uid,jdbcType=INTEGER}, 
  #{score,jdbcType=INTEGER}, #{rank,jdbcType=VARCHAR}, #{time,jdbcType=TIMESTAMP}, 
  #{status,jdbcType=INTEGER}, #{user1,jdbcType=VARCHAR}, #{user2,jdbcType=VARCHAR}, 
  #{user3,jdbcType=VARCHAR}, #{user4,jdbcType=VARCHAR}, #{user5,jdbcType=VARCHAR}
  )

mapper.select:
SELECT a.rankName,COUNT(a.rankName)AS workCount FROM ( SELECT workgrade.`rank`AS rankName FROM `workgrade` INNER JOIN participationworkstu ON `participationworkstu`.`wId` = workgrade.`wId` INNER JOIN `participationworkclass` ON `participationworkclass`.`id` = `participationworkstu`.`user1` INNER JOIN `clazz` ON `clazz`.`id` = participationworkclass.`cId` WHERE workgrade.`uId` = #{sId} #学员id
AND clazz.`gid` = #{gId}
GROUP BY `workgrade`.`id` )a GROUP BY a.rankName

SELECT IFNULL(( ( #已经完成作业次数 SELECT COUNT(*)AS finishCount FROM `workgrade` INNER JOIN `workinstance` ON `workinstance`.id = `workgrade`.wId INNER JOIN `participationworkstu` ON `participationworkstu`.wId = `workgrade`.wId INNER JOIN `clazz` ON `clazz`.`id` = participationworkstu.`user1`#年级 WHERE workgrade.uId = #{sId} #学员id
AND clazz.`gid` = #{gId}
AND workinstance.`endTime` >= participationworkstu.`user2`#作业是否及时完成 (及时完成次数) ) / ( #总过次数 SELECT COUNT(*)AS totalCount FROM `workgrade` INNER JOIN `workinstance` ON `workinstance`.id = `workgrade`.wId INNER JOIN `participationworkstu` ON `participationworkstu`.wId = `workgrade`.wId INNER JOIN `clazz` ON `clazz`.`id` = participationworkstu.`user1`#年级 WHERE workgrade.uId = #{sId} #学员id
AND clazz.`gid` = #{gId}
AND workinstance.`endTime` <= NOW() #到了上交时间 ) *100),0) AS workTimelyFinishRate
