mapper.select:
SELECT count(*) FROM workinstance INNER JOIN workmould ON workmould.`id`=workinstance.`wid` INNER JOIN participationworkclass ON workinstance.`id`=participationworkclass.`wId` INNER JOIN clazz ON clazz.`id`=participationworkclass.cid AND workinstance.`tid`=#{tid} AND workinstance.`status`=0
and participationworkclass.`wStatus`=#{wStatus}
and participationworkclass.`wStatus` in(0,1,2,3)

SELECT workinstance.`id`,workmould.`name`,workinstance.`startTime`,workinstance.`endTime`,clazz.`id` as cid,clazz.`cname`,participationworkclass.`wStatus`,workmould.`wType` FROM workinstance INNER JOIN workmould ON workmould.`id`=workinstance.`wid` INNER JOIN participationworkclass ON workinstance.`id`=participationworkclass.`wId` INNER JOIN clazz ON clazz.`id`=participationworkclass.cid AND workinstance.`tid`=#{tid} AND workinstance.`status`=0
and participationworkclass.`wStatus`=#{wStatus}
and participationworkclass.`wStatus` in(0,1,2,3)
GROUP BY participationworkclass.`id` ORDER BY workinstance.`endTime` ASC LIMIT #{cur},#{pageSize}

  		
SELECT workmould.`score` FROM workinstance
		INNER JOIN workmould ON workinstance.`wid`=workmould.`id`
		WHERE workinstance.`id`=#{id}
